    def main_menu(self):
        step = 1
        value = 0
        max_value = 3
        last_time = time.ticks_ms()
        def update_menu_option(index, option_text):
            if value == index:
                word = f"[ {option_text} ]"
            else:
                word = f" {option_text} "
            self.oled.text(word, 0, line_height * index, 1)

        def update_menu():
            options = ["HR", "HRV", "Kubios", "History"]
            self.oled.fill(0)
            for i, option in enumerate (options):
                update_menu_option(i, option)
            self.oled.show()
        
        def countdown_display():
            if value == 0:
                for i in range(10, 0, -1):
                    self.oled.fill(0)
                    self.oled.text(f"{i}s to go...", 24, 31, 1)
                    self.oled.show()
                    time.sleep(0.3)
                
                self.HR()
            elif value == 3:
                for i in range(10, 0, -1):
                    self.oled.fill(0)
                    self.oled.text(f"{i}s to go...", 24, 31, 1)
                    self.oled.show()
                    time.sleep(0.3)
                
                self.history()
            update_menu()
        update_menu()    
        
        while True:
            while rotary.fifo.has_data():
                direction = rotary.fifo.get()
                if direction == 1:
                    value = min(value + step, max_value)
                elif direction == -1:
                    value = max(value - step, 0)
                elif direction == 2:
                    now = time.ticks_ms()
                    
                    if time.ticks_diff(now, last_time) > 50:
                        countdown_display()
                        last_time = now
                update_menu()
        
   
    def history(self):
        measurements = ["Measurement 1", "Measurement 2", "Measurement 3", "Measurement 4", "Measurement 5"]
        
        step = 1
        value = 0
        max_value = len(measurements) - 1
        selected_index = 0
        last_rotary = rot_a.value()
        last_button = encoder_button.value()
        last_time = time.ticks_ms()
        bear = bytearray([
            0x1F, 0x61, 0x8B, 0xA2, 0xA2, 0x8B, 0x61, 0x1F
            ])
        bear_framebuf = framebuf.FrameBuffer(bear, 8, 8, framebuf.MONO_VLSB)
    
        
        def update_menu():
            oled.fill(0)
            for i, option in enumerate(measurements):
                if i == selected_index:
                    oled.blit(bear_framebuf, 0, i * line_height)
                oled.text(option, 15, i * line_height)
            
            oled.show()
        update_menu()
        
        while True:	
            current_rot = rot_a.value()
            if current_rot != last_rotary:
                if rot_b.value() != current_rot:
                    value = (value - 1) % (max_value + 1)
                else:
                    value = (value + 1) % (max_value + 1)
                selected_index = value
                update_menu()
                last_rotary = current_rot
                time.sleep(0.05)

            current_button = encoder_button.value()
            if current_button == 0 and last_button == 1:
                #selected_index=history.measurement_data
                now = time.ticks_ms()
                if time.ticks_diff(now, last_time) > 49:  # debounce
                    oled.fill(0)
                    oled.text("Selected:", 0, 0)
                    oled.text(f"measurements {value + 1}", 0, 16)
                    oled.show()
                    time.sleep(1.5)
                    update_menu()
                last_time = now
            last_button = current_button
            time.sleep(0.05)

    def HR(self):
        scroll_y = 0
        def timestamp():
            ntptime.settime() # to take the real time from the internet
            t = utime.localtime()
            year = str(t[0])
            month = str(t[1])
            day = str(t[2])
            hour = str(t[3] + 3)
            minute = str(t[4])
            second = str(t[5])
            
            if len(month) == 1:
                month = "0" + month
            if len(day) == 1:
                day = "0" + day
            if len(hour) == 1:
                hour = "0" + hour
            if len(minute) == 1:
                minute = "0" + minute
            if len(second) == 1:
                second = "0" + second
                
            time = day + "-" + month + "-" + year + " " + hour + ":" + minute + ":" + second
            
            return time
        
        y = 0
        def display(y):
            color = 1
            oled.fill(0)
            
            oled.text(timestamp(), 0, line_height * 0 - y)
            oled.text('Mean HR: 75',0 , line_height * 1 - y)
            oled.text('Mean PPI: 750', 0, line_height*2 - y)
            oled.text('RMSSD: 23', 0, line_height*3 - y)
            oled.text('SDNN: 22', 0, line_height*4 - y)
            oled.text('SNS: 1.234', 0, line_height*5 - y)
            oled.text('PNS: -1.234', 0, line_height*6 - y)
            oled.show()
        display(y)
        
        while True:
            while rotary.fifo.has_data():
                direction = rotary.fifo.get()
                if direction == -1:
                    y = max(0, y -10)
                elif direction == 1:
                    y = min(64, y + 10)
                elif direction == 2:
                    return
                display(y)
            time.sleep(0.05)
                    
                
            

        
